import { Popover, OverlayTrigger, Nav } from "react-bootstrap";

const popover = (
  <Popover id="popover-basic">
    <Popover.Title as="h3">About</Popover.Title>
    <Popover.Content>
    <a href="https://github.com/EBEREGIT/COVID-19-barter-trade">COVID-19 Barter Trade</a> is an innovation brought about due to the fact that Food and other essentials are quite expensive during the COVID-19 pandemic.
      </Popover.Content>
      <Popover.Content>
      I thought it is going to be easier and cheaper if people could exchange what they have for other things that they do not have. This application is provided to connect people for such transactions.
      </Popover.Content>
      <Popover.Content>
      You may also love to checkout the <a href="https://covid19-stat.netlify.com/">COVID-19 TRACKER</a> I created a while ago.
    </Popover.Content>
    <Popover.Content>
        Created By <a href="https://www.njokusamsonebere.com/">Njoku Samson Ebere</a>
    </Popover.Content>
  </Popover>
);

export default function About() {
  return (
    <div>
      <OverlayTrigger trigger="click" placement="bottom" overlay={popover}>
        <Nav.Link eventKey={3} href="#memes">
          <i class="fa fa-info-circle" aria-hidden="true"></i>{" "}
          <span>About</span>
        </Nav.Link>
      </OverlayTrigger>
    </div>
  );
}
